function sanitizeFileName(title: string, fallbackId: string): string {
  const sanitized = title
    .replace(/[<>:"/\\|?*]/g, '-') // 파일명 금지 문자 처리
    .replace(/\s+/g, '-') // 공백을 대시로
    .replace(/-+/g, '-') // 연속된 대시를 하나로
    .replace(/^-|-$/g, '') // 시작/끝 대시 제거
    .toLowerCase();

  // 빈 문자열이거나 너무 긴 경우 ID 사용
  if (!sanitized || sanitized.length > 50) {
    return fallbackId;
  }

  return sanitized;
}

